    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
        // TODO add your handling code here:
        String no1 = number1.getText();
        String no2 = number2.getText();
        double dno1 = Double.parseDouble(no1);
        double dno2 = Double.parseDouble(no2);
        double re = add(dno1, dno2);
        String sResult = String.valueOf(re);
        jTextField3.setText(sResult);
    }//GEN-LAST:event_addActionPerformed

