    private double add(double dno1, double dno2) {
        webservice.Calculate_Service service = new webservice.Calculate_Service();
        webservice.Calculate port = service.getCalculatePort();
        return port.addition(dno1,dno2);
    }

    private double sub(double dno1, double dno2) {
        webservice.Calculate_Service service = new webservice.Calculate_Service();
        webservice.Calculate port = service.getCalculatePort();
        return port.substraction(dno1, dno2);
    }

    private double mul(double dno1, double dno2) {
        webservice.Calculate_Service service = new webservice.Calculate_Service();
        webservice.Calculate port = service.getCalculatePort();
        return port.multiplication(dno1, dno2);
    }

    private String div(double dno1, double dno2) {
        webservice.Calculate_Service service = new webservice.Calculate_Service();
        webservice.Calculate port = service.getCalculatePort();
        return port.divisio(dno1, dno2);
    }
